const prompt = require('prompt-sync')();

let month = Number(prompt("Digite o numero do mes: "));


switch (month) {
    case 1:
    case 3:
    case 5:
    case 7:
    case 10:
    case 12:
        console.log("São 31 dias");
        break;
    case 4:
    case 6:
    case 9:
    case 11:
        console.log("São 30 dias");
        break;
    default:
        console.log("Mês inválido");
}
