//2. Encontre o Maior Número em um Array

let maior = 0;

function buscarMaior (numero){
    for(let i=0; i<numeros.length; i++)
        if(numero[i]>maior){
            maior = numero[i]
        }
        return maior
}

resultado = buscarMaior(numeros)
console.log(resultado)


