/*
    Objetivo: Contar a quantidade de filmes por gênero.
    Use o método forEach para iterar sobre o array filmes.
    Para cada filme, verifique se o gênero já existe no objeto de contagem. 
    Se existir, incremente a contagem; se não, adicione o gênero ao objeto com a contagem inicial de 1.
*/

const movies = [
    {title:"O Hobbit",  genre: "Fantasy" , year: 1925},
    {title:"Harry Potter", genre: "Adventure" , year: 1999},
    {title:"Nárnia", genre: "Adventure" , year: 2010},
    {title:"Velozes e Furiosos", genre: "Ruim" , year: 2010},
    {title:"A Fantastica Fábrica de Chocolate", genre: "Adventure" , year: 2010}
]

let countGenre= {};

movies.forEach(movie => {
    if(countGenre[movie.genre]){
        countGenre[movie.genre]++
    }
    else{
        countGenre[movie.genre] = 1
    }
});

console.log(countGenre)

for(let genre in countGenre){
    console.log(`Existem ${countGenre[genre]} do genero ${genre}`)
}